import React, { useState } from "react";
import Header from "./header.js";
import Footer from "./footer.js";
import { Link } from "react-router-dom";

export default function cart() {
  let vendor = JSON.parse(localStorage.getItem("cart_info"));

  if (vendor.length == 0) {
    return (
      <div>
        No items selected for cart{" "}
        <Link to="/">
          <button>Return</button>
        </Link>
      </div>
    );
  } else {
    return (
      <div>
        <Header />
        <div className="bodybuttons">
          <Link to="/">
            <button>All Products</button>
          </Link>

          <Link to="/myproducts">
            <button>My Products</button>
          </Link>

          {localStorage.getItem("vendor-info") ? (
            <Link to="/addproduct">
              <button>Add Product</button>
            </Link>
          ) : (
            <div></div>
          )}
        </div>

        <div>
          {vendor.length > 0 && (
            <div className="myproductsbody">
              <div className="mypdheader">
                <p>My Cart</p>
              </div>
              {vendor.map((item) => (
                <>
                  <div className="prodelements">
                    <div className="mypdlist">
                      <div className="mypdimagediv">
                        <img
                          src={"http://127.0.0.1:8000/" + item.pdimg}
                          className="mypdimage"
                          alt="Logo"
                        />
                      </div>
                      <div className="mypddetails">
                        {/* <p>User Id: {item.user_id}</p> */}
                        <h2>{item.product}</h2>
                        <div className="mycartdetails">
                          <div>
                            <p>Manufacturer: {item.manufacturer}</p>
                            <p>Price: {item.price}</p>
                            <p>Quantity: {item.quantity}</p>
                          </div>
                          <div>
                            <p>Tags: {item.tags}</p>
                            <p>Description: {item.description}</p>
                          </div>
                        </div>
                      </div>
                      <div className="mycartaction">
                        <button>+</button>1<button>-</button>
                        <br />
                        <br />
                        Subtotal: 25000
                      </div>
                    </div>
                  </div>
                </>
              ))}
            </div>
          )}
        </div>
        <Footer />
      </div>
    );
  }
}
