import react, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const navigate = useNavigate();

  // Determining the fields required
  const [formData, setFormData] = react.useState({
    email: "",
    password: "",
  });
  const [formErrors, setFormErrors] = react.useState({});
  const [isSubmit, setIsSubmit] = react.useState(false);

  // every change is attributed to name field of input tag and value is changed
  function handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;
    setFormData((preValue) => {
      return {
        ...preValue,
        [name]: value,
      };
    });
  }

  function handleSubmit(event){
    event.preventDefault();
    setFormErrors(validate(formData));
    setIsSubmit(true);
  }



function validate(values){
  const errors = {};
  if(!values.email){
    errors.email = "Enter your email"
  }
  if(!values.password){
    errors.password = "Enter your password"
  }else if(values.password.length < 4){
    errors.password = "Password length must be more that 4"
  }
  return errors;
}

useEffect(()=>{
  console.log(formErrors);
  if(Object.keys(formErrors).length === 0 && isSubmit){
    console.log("Success");
  }
})



  return (
    <form onSubmit={handleSubmit}>
      <div className="loginform">
      </div>
      <div className="innerloginform">
        <div className="displayform"><h2>Login Form</h2></div>
          <div className="actualform">
          <div className="inputdiv">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
            />
            <p classname="errorsmsg" style={{color:"red", fontSize:"17px"}}>{formErrors.email}</p>
          </div>

          <div  className="inputdiv">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
            />
            <p classname="errorsmsg" style={{color:"red", fontSize:"17px", marginTop: "0px"}}>{formErrors.password}</p>
          </div>

          <div  className="inputdiv">
            <button type="submit">Log In</button>
          </div>

          <div className="loginbelow">
            <p>
              Don't have an account?
              <a href="/registration"> Register Here</a>
            </p>
          </div>
        </div>
        </div>
    </form>
  );
}
