import { useEffect, useState } from 'react';
import React from 'react'
import { useParams, useNavigate } from 'react-router-dom'

export default function update() {
    const params = useParams();
    // console.log(params.id);

    const [productData, setProductData] = useState([]);
    const navigate = useNavigate();
  
    const fetchProductData = () => {
      fetch("http://127.0.0.1:8000/api/getproduct/"+params.id)
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          setProductData(data);
        });
    };
  
    useEffect(() => {
      fetchProductData();
    }, []);



    return (
        <div>
          {localStorage.getItem("vendor-info") ? (
            <form>
              <div>
                <label for="product">Product Name</label>
                <input type="text" onChange={(event) => setProduct(event.target.value)} name="product" value={productData.product} />
              </div>
    
              <div>
                <label for="title">Manufacturer</label>
                <input
                  type="text"
                  name="manufacturer"
                  placeholder="Example: Audi"
                  value={productData.manufacturer}
                  onChange={(event) => setManufacturer(event.target.value)}
                />
              </div>
    
              <div>
                <label for="price">Price</label>
                <input type="text" value={productData.price} onChange={(event) => setPrice(event.target.value)} name="price" placeholder="In Nrs." />
              </div>
    
              <div>
                <label for="quantity">Quantity</label>
                <input type="text" value={productData.quantity} onChange={(event) => setQuantity(event.target.value)} name="quantity" />
              </div>
    
              <div>
                <label for="tags">Tags (Comma Separated)</label>
                <input
                  type="text"
                  name="tags"
                  value={productData.tags}
                  onChange={(event) => setTags(event.target.value)}
                  placeholder="Example: Camera, Lens, Photograph etc"
                />
              </div>
    
              <div>
                <label for="productimage">Product Image</label>
                <input type="file"  onChange={(event) => setPdimg(event.target.files[0])} name="pdimg" />
              </div>
    
              <div>
                <label for="description">Job Description</label>
                <textarea
                  name="description"
                  rows="10"
                  value={productData.description}
                  onChange={(event) => setDescription(event.target.value)}
                  placeholder="Include product's details"
                ></textarea>
              </div>
    
              <button>
                Add Product
              </button>
    
              <a href="/">
                
                Back
              </a>
            </form>
          ) : (
            <div>
              {useEffect(() => {
                navigate("/login");
              })}
            </div>
          )}
        </div>
      );
}
