import React from "react";
import { useState } from "react";

function subtotal(props) {
    let price = props.data;

  const [count, setCount] = useState(1);
const [total, setTotal] = useState(0);
  function handleIncrement() {
    setCount((prevCount) => {
      return (prevCount += 1);
    });
    setTotal((preValue) => {
      return(preValue = preValue + (count*price))});
  }
  console.log(total);
  function handleDecrement() {
    setCount((prevCount) => {
      if (prevCount > 1) {
        return (prevCount -= 1);
      } else {
        return (prevCount = 1);
      }
    });
    setTotal(total = total + (count * price));
    console.log(total);  
  }

  return (
    <div className="mycartaction">
      <div className="fornumber">
        <div class="number">
          <span class="minus" onClick={handleDecrement}>
            -
          </span>

          <input type="text" value={count} />
          <span class="plus" onClick={handleIncrement}>
            +
          </span>
        </div>
      </div>
      <br />
      <br />
      <div id="subtotaldiv">
      Subtotal: {count * price}
      </div>
    </div>
  );
}

export default subtotal;
