import { useEffect, useState } from "react";
import react from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function update() {
  if (localStorage.getItem("vendor-info")) {
    const params = useParams();
    // console.log(params.id);

    const [productData, setProductData] = useState([]);

    const fetchProductData = () => {
      fetch("http://127.0.0.1:8000/api/getproduct/" + params.id)
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          setProductData(data);
        });
    };

    useEffect(() => {
      fetchProductData();
    }, []);


    async function handleChanges(event) {
      event.preventDefault();
      // console.log(productData);
      let result = await fetch("http://127.0.0.1:8000/api/updateproduct/"+params.id+"?_method=PUT",{
        method: "PUT",
        body: productData
    });
    alert("Product updated");
    navigate("/myproducts");
    }

    return (
      <div>
        <form onSubmit={handleChanges}>
          <div>
            <label for="product">Product Name</label>
            <input
              type="text"
              onChange={(event) => setProductData({...productData, product: event.target.value})}
              // onChange={(event) => setProduct(preValue =>{ return{...preValue, [name]:event.target.value}})}
              name="product"
              defaultValue={productData.product}
              
            />
          </div>

          <div>
            <label for="title">Manufacturer</label>
            <input
              type="text"
              name="manufacturer"
              placeholder="Example: Audi"
              defaultValue={productData.manufacturer}
              onChange={(event) => setProductData({...productData, manufacturer: event.target.value})}
            />
          </div>

          <div>
            <label for="price">Price</label>
            <input
              type="text"
              defaultValue={productData.price}
              onChange={(event) => setProductData({...productData, price: event.target.value})}
              name="price"
              placeholder="In Nrs."
            />
          </div>

          <div>
            <label for="quantity">Quantity</label>
            <input
              type="text"
              defaultValue={productData.quantity}
              onChange={(event) => setProductData({...productData, quantity: event.target.value})}
              name="quantity"
            />
          </div>

          <div>
            <label for="tags">Tags (Comma Separated)</label>
            <input
              type="text"
              name="tags"
              defaultValue={productData.tags}
              onChange={(event) => setProductData({...productData, tags: event.target.value})}
              placeholder="Example: Camera, Lens, Photograph etc"
            />
          </div>

          <div>
            <label for="productimage">Product Image</label>
            <input
              type="file"
              defaultValue={productData.pdimg}
              onChange={(event) => setProductData({...productData, pdimg: event.target.files[0]})}
              name="pdimg"
            />
            <br />
            <br />
            <img
              src={"http://127.0.0.1:8000/" + productData.pdimg}
              className="pdimage"
              alt="Logo"
            />
          </div>

          <div>
            <label for="description">Job Description</label>
            <textarea
              name="description"
              rows="10"
              defaultValue={productData.description}
              onChange={(event) => setProductData({...productData, description: event.target.value})}
              placeholder="Include product's details"
            ></textarea>
          </div>

          <button>Update Product</button>

          <a href="/">Back</a>
        </form>
      </div>
    );
  } else {
    const navigate = useNavigate();
    {
      useEffect(() => {
        navigate("/login");
      });
    }
  }
}
