import react, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const navigate = useNavigate();

  // Determining the fields required
  const [formData, setFormData] = react.useState({
    email: "",
    password: "",
  });

  // every change is attributed to name field of input tag and value is changed
  function handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;
    setFormData((preValue) => {
      return {
        ...preValue,
        [name]: value,
      };
    });
  }

  function handleSubmit(event){
    event.preventDefault();
    validation(formData);
  }


  return (
    <form onSubmit={handleSubmit}>
      <div className="loginform">
      </div>
      <div className="innerloginform">
        <div className="displayform"><h2>Login Form</h2></div>
          <div className="actualform">
          <div className="inputdiv">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
            />
          </div>

          <div  className="inputdiv">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
            />
          </div>

          <div  className="inputdiv">
            <button type="submit">Log In</button>
          </div>

          <div className="loginbelow">
            <p>
              Don't have an account?
              <a href="/registration"> Register Here</a>
            </p>
          </div>
        </div>
        </div>
    </form>
  );
}
