import { useEffect, useState } from "react";
import react from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function update() {
  if (localStorage.getItem("vendor-info")) {
    const params = useParams();
    // console.log(params.id);
    const [formData, setFormData] = react.useState();

    const [productData, setProductData] = useState([]);

    const fetchProductData = () => {
      fetch("http://127.0.0.1:8000/api/getproduct/" + params.id)
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          setProductData(data);
        });
    };

    useEffect(() => {
      fetchProductData();
    }, []);
    // console.log(productData);
    let iniProd = productData.product;


    const [product, setProduct] = react.useState();
    // console.log(product);
    const [manufacturer, setManufacturer] = react.useState();
    const [price, setPrice] = react.useState();
    const [quantity, setQuantity] = react.useState();
    const [tags, setTags] = react.useState();
    const [pdimg, setPdimg] = react.useState();
    const [description, setDescription] = react.useState();

    function handleChanges(event) {
      event.preventDefault();

      // const formData = new FormData();
      // formData.append("product", product);
      // formData.append("manufacturer", manufacturer);
      // formData.append("price", price);
      // formData.append("quantity", quantity);
      // formData.append("tags", tags);
      // formData.append("pdimg", pdimg);
      // formData.append("description", description);
      // console.log(
      //   product,
      //   manufacturer,
      //   price,
      //   quantity,
      //   tags,
      //   pdimg,
      //   description
      // );

      // console.log(formData);
    }

    function handleChange(event){
      const {name, value} = event.target
      setFormData(productData.product)
      setFormData(preValue => {
          return{
              ...preValue,
              [name]: "productData.product"
          }
      })

  }
  console.log(FormData)


    return (
      <div>
        <form onSubmit={handleChanges}>
          <div>
            <label for="product">Product Name</label>
            <input
              type="text"
              onChange={handleChange} 

              // onChange={(event) => setProduct(preValue =>{ return{...preValue, [name]:event.target.value}})}
              name="product"
              Value={productData.product}
 

            />
          </div>

          <div>
            <label for="title">Manufacturer</label>
            <input
              type="text"
              name="manufacturer"
              placeholder="Example: Audi"
              defaultValue={productData.manufacturer}
              onChange={(event) => setManufacturer(event.target.value)}
            />
          </div>

          <div>
            <label for="price">Price</label>
            <input
              type="text"
              defaultValue={productData.price}
              onChange={(event) => setPrice(event.target.value)}
              name="price"
              placeholder="In Nrs."
            />
          </div>

          <div>
            <label for="quantity">Quantity</label>
            <input
              type="text"
              defaultValue={productData.quantity}
              onChange={(event) => setQuantity(event.target.value)}
              name="quantity"
            />
          </div>

          <div>
            <label for="tags">Tags (Comma Separated)</label>
            <input
              type="text"
              name="tags"
              defaultValue={productData.tags}
              onChange={(event) => setTags(event.target.value)}
              placeholder="Example: Camera, Lens, Photograph etc"
            />
          </div>

          <div>
            <label for="productimage">Product Image</label>
            <input
              type="file"
              defaultValue={productData.pdimg}
              onChange={(event) => setPdimg(event.target.files[0])}
              name="pdimg"
            />
            <br />
            <br />
            <img
              src={"http://127.0.0.1:8000/" + productData.pdimg}
              className="pdimage"
              alt="Logo"
            />
          </div>

          <div>
            <label for="description">Job Description</label>
            <textarea
              name="description"
              rows="10"
              defaultValue={productData.description}
              onChange={(event) => setDescription(event.target.value)}
              placeholder="Include product's details"
            ></textarea>
          </div>

          <button>Add Product</button>

          <a href="/">Back</a>
        </form>
      </div>
    );
  } else {
    const navigate = useNavigate();
    {
      useEffect(() => {
        navigate("/login");
      });
    }
  }
}
