import React from "react";
import { useState } from "react";
function subtotal() {
  const [count, setCount] = useState(1);

  function handleIncrement(props) {
    let price = props.data;
    setCount((prevCount) => {
      return (prevCount += 1);
    });
  }

  function handleDecrement() {
    setCount((prevCount) => {
      if (prevCount > 0) {
        return (prevCount -= 1);
      } else {
        return (prevCount = 0);
      }
    });
  }

  return (
    <div className="mycartaction">
      <div className="fornumber">
        <div class="number">
          {/* <span class="minus" onClick={handleDecrement}>-</span> */}
          <span class="minus" onClick={handleDecrement}>
            -
          </span>

          <input type="text" value={count} />
          <span class="plus" onClick={handleIncrement}>
            +
          </span>
        </div>
      </div>
      <br />
      <br />
      Subtotal: {count * props.price}
    </div>
  );
}

export default subtotal;
