import React, { useState, useEffect } from "react";

export default function search(props) {
  let searchvalue = props.data;
  // console.log(searchvalue)
  const [searchResult, setSearchResult] = useState([]);
  const handleSearch = () => {
    fetch("http://127.0.0.1:8000/api/search/" + searchvalue)
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setSearchResult(data);
      });
  };

  useEffect(() => {
    handleSearch();
  }, [searchvalue]);

  // console.log(searchResult);

  return (
<div className="allproducts">
<div className="cartbutton">
  <p>All Products</p>
  <div className="cart">
    <Link to="/cart">
      <button>
        <i class="fa-solid fa-cart-shopping"></i> {cartNumber}
      </button>
    </Link>
  </div>
</div>
{searchResult.length > 0 && (
  <div className="productbody">
    {searchResult.map((item) => (
      <div className="pdlist">
        <div className="pdimage">
        <img
          src={"http://127.0.0.1:8000/" + item.pdimg}
          className="pdimage"
          alt="Logo"
        />
        </div>
        <div className="productcard">
          <h2>{item.product}</h2>
          <div className="productdetails">
            <div className="leftdetails">
              <p>Manufacturer: {item.manufacturer}</p>
              <p>Price: {item.price}</p>
              <p>Quantity: {item.quantity}</p>
            </div>
            <div className="rightdetails">
              <p>Tags: {item.tags}</p>
              <p>Description: {item.description}</p>
            </div>
          </div>
          
        </div>
        <div className="pdbutton">
            <button onClick={() => handleCart(item)}>
              <i class="fa-solid fa-cart-plus"></i>Add to cart
            </button>
          </div>
      </div>
    ))}
  </div>
)}
</div>
  );
}
