import { useEffect } from "react";
import react from "react";
import { useNavigate } from "react-router-dom";

export default function AddProduct() {
  const navigate = useNavigate();

// Determining the fields required
const [formData, setFormData] = react.useState({
    product: "",
    pdimg: "",
    tags: "",
    manufacturer: "",
    price: "",
    quantity: "",
    description: "",
  });
  const[forFile, setForFIle] = react.useState();

  // every change is attributed to name field of input tag and value is changed
  function handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;
    setFormData((preValue) => {
      return {
        ...preValue,
        [name]: value,
      };
    });
  }

  async function handleSubmit(event) {
    event.preventDefault(); // to stop refreshing
    // if (formData.password != formData.password_confirmation) {
    //   alert("Password Doesnt match");
    // } else { // actual passing of data through api
    //   // console.log(formData);
    //   let result = await fetch("http://127.0.0.1:8000/api/register",{
    //     method:'POST',
    //     body:JSON.stringify(formData),
    //     headers:{
    //       "Content-Type":'application/json',
    //       "Accept":'application/json'
    //     }
    //   })
    //   result = await result.json()
    // setFormData((preValue) => {
    //     return{
    //         ...preValue,
    //         ['pdimg']:forFile
    //     }
    // })
    let result = await fetch("http://127.0.0.1:8000/api/addproduct",{
        method:'POST',
        body:formData
    });
    alert("Data has been saved")
    // console.log(formData)
    //   localStorage.setItem("vendor-info",JSON.stringify(result))
    //   alert("Registration Successful. Re-Login");
    //   navigate('/login');
    }
//   }


  return (
    <div>
      {localStorage.getItem("vendor-info") ? (
        <form onSubmit={handleSubmit}>
          <div>
            <label for="product">Product Name</label>
            <input type="text" onChange={handleChange} name="product" />
          </div>

          <div>
            <label for="title">Manufacturer</label>
            <input
              type="text"
              name="manufacturer"
              placeholder="Example: Audi"
              onChange={handleChange}
            />
          </div>

          <div>
            <label for="price">Price</label>
            <input type="text" onChange={handleChange} name="price" placeholder="In Nrs." />
          </div>

          <div>
            <label for="quantity">Quantity</label>
            <input type="text" onChange={handleChange} name="quantity" />
          </div>

          <div>
            <label for="tags">Tags (Comma Separated)</label>
            <input
              type="text"
              name="tags"
              onChange={handleChange}
              placeholder="Example: Camera, Lens, Photograph etc"
            />
          </div>

          <div>
            <label for="productimage">Product Image</label>
            <input type="file" onChange={handleChange} name="pdimg" />
          </div>

          <div>
            <label for="description">Job Description</label>
            <textarea
              name="description"
              rows="10"
              onChange={handleChange}
              placeholder="Include product's details"
            ></textarea>
          </div>

          <button>
            Add Product
          </button>

          <a href="/">
            
            Back
          </a>
        </form>
      ) : (
        <div>
          {useEffect(() => {
            navigate("/login");
          })}
        </div>
      )}
    </div>
  );
}
