import React, { useEffect, useState } from "react";
import Header from "./header.js";
import Footer from "./footer.js";
import { Link } from "react-router-dom";

export default function cart() {

  const[count, setCount] = useState(0);
  const [subTotal, setSubTotal] = useState(1);

  function handleIncrement(price) {
    // console.log(price)
    setCount((prevCount) => {
      return (prevCount += 1);
    });
    setSubTotal(count * price);
  }

  function handleDecrement(price) {
    setCount((prevCount) => {
      if (prevCount > 0) {
        return (prevCount -= 1); 
      } else {
        return (prevCount = 0);
      }
    });
    // setSubTotal(count * price);
  }
  {  useEffect(() => {
    // handleDecrement(),
    // handleIncrement()
  }, [count])}

  // function handleSubTotal(){
  //   let product = JSON.parse(localStorage.getItem("cart_info"));
  //   setSubTotal(count * product[0].price)
  // }


  let vendor = JSON.parse(localStorage.getItem("cart_info"));

  if (vendor.length == 0) {
    return (
      <div>
        No items selected for cart{" "}
        <Link to="/">
          <button>Return</button>
        </Link>
      </div>
    );
  } else {
    return (
      <div>
        <Header />
        <div className="bodybuttons">
          <Link to="/">
            <button>
              <i class="fa-solid fa-server"></i>All Products
            </button>
          </Link>

          <Link to="/myproducts">
            <button>
              <i class="fa-solid fa-box-open"></i>My Products
            </button>
          </Link>

          {localStorage.getItem("vendor-info") ? (
            <Link to="/addproduct">
              <button>
                <i class="fa-solid fa-square-plus"></i>Add Product
              </button>
            </Link>
          ) : (
            <div></div>
          )}
        </div>

        <div>
          {vendor.length > 0 && (
            <div className="myproductsbody">
              <div className="mypdheader">
                <p>My Cart</p>
              </div>
              <div className="mycardcard">
                {vendor.map((item) => (
                  <>
                    <div className="prodelements">
                      <div className="mypdlist">
                        <div className="mypdimagediv">
                          <img
                            src={"http://127.0.0.1:8000/" + item.pdimg}
                            className="mypdimage"
                            alt="Logo"
                          />
                        </div>
                        <div className="mypddetails">
                          {/* <p>User Id: {item.user_id}</p> */}
                          <h2>{item.product}</h2>
                          <div className="mycartdetails">
                            <div>
                              <p>Manufacturer: {item.manufacturer}</p>
                              <p>Price: {item.price}</p>
                              <p>Quantity: {item.quantity}</p>
                            </div>
                            <div>
                              <p>Tags: {item.tags}</p>
                              <p>Description: {item.description}</p>
                            </div>
                          </div>
                        </div>
                        <div className="mycartaction">
                          <div className="fornumber">
                            <div class="number">
                            
                              {/* <span class="minus" onClick={handleDecrement}>-</span> */}
                              <span class="minus" onClick={()=>handleDecrement(item.price)}>-</span>

                              <input type="text" value={count} />

                              <span class="plus" onClick={() => handleIncrement(item.price)}>+</span>
                            </div>
                          </div>
                          <br />
                          <br />
                          
                          Subtotal: {subTotal}
                        </div>
                      </div>
                    </div>
                  </>
                ))}
              </div>
                <div className="grandtotalcart">Grand Total: 255948000 Nrs.</div>
                <div className="checkoutbutton">
                  <button>Check-out</button>
                </div>
            </div>
          )}
        </div>
        <Footer />
      </div>
    );
  }
}
