import React, { useState, useEffect } from 'react'

export default function search(props) {

  const [result, setResult] = useState();
    const handleSearch = () => {
        fetch("http://127.0.0.1:8000/api/search/"+props.data)
          .then((response) => {
            return response.json();
          })
          .then((data) => {
            setResult(data);
          });
      }; 
      
      useEffect(() => {
        handleSearch();
      }, []);
      



  return (
    <div>{result.product}</div>
  )
}
