import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function AllProducts() {
  const [productData, setProductData] = useState([]);
  const [cartValue, setCartValue] = useState([]);
  const [cartNumber, setCartNumber] = useState(0);
  const fetchProductData = () => {
    fetch("http://127.0.0.1:8000/api/allproducts")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setProductData(data);
      });
  };

  useEffect(() => {
    fetchProductData();
  }, []);

  function handleCart(id) {
    setCartValue([...cartValue, id]);
    setCartNumber(cartNumber + 1);
    alert("Item added to cart");
  }
  localStorage.setItem("cart_info", JSON.stringify(cartValue));

  return (
    <div className="allproducts">
      <div className="cartbutton">
        <p>All Products</p>
        <div className="cart">
          <Link to="/cart">
            <button>
              <i class="fa-solid fa-cart-shopping"></i> {cartNumber}
            </button>
          </Link>
        </div>
      </div>
      {productData.length > 0 && (
        <div className="productbody">
          {productData.map((item) => (
            <div className="pdlist">
              <img
                src={"http://127.0.0.1:8000/" + item.pdimg}
                className="pdimage"
                alt="Logo"
              />
              <div className="productcard">
                <h2>{item.product}</h2>
                <div className="productdetails">
                  <div className="leftdetails">
                    <p>Manufacturer: {item.manufacturer}</p>
                    <p>Price: {item.price}</p>
                    <p>Quantity: {item.quantity}</p>
                  </div>
                  <div className="rightdetails">
                    <p>Tags: {item.tags}</p>
                    <p>Description: {item.description}</p>

                    
                  </div>
                  
                </div>
                <div>
                      <button onClick={() => handleCart(item)}>
                        <i class="fa-solid fa-cart-plus"></i>Add to cart
                      </button>
                    </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
